# https://www.hackerrank.com/challenges/merge-the-tools/problem
def merge_the_tools(string, k):
    steps = len(string)//k
    for i in range(steps):
        testStr = string[k*i:k*(i+1)]
        newStr = ""
        for j in testStr:
            if j in newStr:
                newStr = newStr
            else:
                newStr = newStr+j
        print(newStr)


merge_the_tools("", 1246)
